export function getSkillDescriptionTranslated(name) {
  if (skillDescriptions[name]) {
    return skillDescriptions[name]
  }
  if (name.includes('/TL')) {
    const secondName = name.split('/TL')[0] + '/TL'
    if (skillDescriptions[secondName]) {
      return skillDescriptions[secondName]
    }
  }

  if (name.includes('(')) {
    const secondName = name.split(' (')[0]
    return getSkillDescriptionTranslated(secondName)
  }

  return ''
}
